import axios from 'axios';

import Thumbnail from '../../components/Thumbnail';

const Home = ({shows}) =>{
    
    const renderShows = () => {
        return shows.map((showItem, index)=>{
            const {show} = showItem;
            console.log(show)
            return <li key={index}><Thumbnail imageURL = {show.image.medium} caption={show.name}/></li>
        })
    }

    return(
        <ul>{renderShows()}</ul>
    );
}

Home.getInitialProps = async context => {
    const country = context.query.country || 'in';
    const response = await axios.get(`http://api.tvmaze.com/schedule?country=${country}&date=2014-12-01`);
    return{
        shows: response.data
    }
}

export default Home;
