import axios from 'axios';

import Thumbnail from '../../components/Thumbnail';
import Header from '../../components/Header';

const Home = ({ shows, country }) => {
    //console.log(shows)    
    const renderShows = () => {
        return shows.map((showItem, index) => {
            const { show } = showItem;
            return <li key={index}>
                <Thumbnail
                    imageURL={(show.image && show.image.medium) || undefined}
                    caption={show.name}
                    href="[country]/[showId]"
                    as={`/${country}/${show.id}`}
                /></li>
        })
    }

    return (
        <div className="home">
            <ul className="tvshows-grid">
                {renderShows()}

                <style jsx>{
                    `.tvshows-grid {
						display: grid;
						grid-template-columns: 1fr 1fr;
                        gap: 10px;
                        list-style-type: none;
                        padding: 0;
                        margin: 0;
				}`}</style>
            </ul>
        </div>
    );
}

Home.getInitialProps = async context => {
    const country = context.query.country || 'in';
    const response = await axios.get(`http://api.tvmaze.com/schedule?country=${country}&date=2014-12-01`);
    return {
        shows: response.data,
        country
    }
}

export default Home;
