import axios from 'axios';
import parse from 'html-react-parser';

import Cast from '../../components/Cast';


const ShowDetails = ({ show = {}, statusCode }) => {
    const { name, image, summary, _embedded } = show;

    if(statusCode){
        return <h3>There is an error</h3>
    }

    return (
        <div className="show-details">
            <div
                className="show-details__poster"
                style={{ backgroundImage: `url(${image.original})` }}>
            </div>
            <h1>{name}</h1>
            {parse(summary)}
            <style jsx>{`
            .show-details__poster{
                height: 200px;
                background-size: cover;
            }`}
            </style>
            {_embedded.cast.length > 0 && <Cast cast={_embedded.cast} />}
        </div>
    );
};

ShowDetails.getInitialProps = async ({ query }) => {

    try {
        const { showId } = query;
        const response = await axios.get(`http://api.tvmaze.com/shows/${showId}?embed=cast`);
        return {
            show: response.data
        };
    }catch(error){
        return{
            statusCode: error.response ? error.response.status : 500
        }
    }
}

export default ShowDetails;
